// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

// Import ReentrancyGuard for security and Ownable for admin control
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title JobBoard
 * @dev A smart contract for posting jobs, escrowing payments, assigning freelancers,
 * handling work submission, payment release, refunds, and dispute resolution.
 * Stores job descriptions off-chain using an IPFS CID.
 */
contract JobBoard is ReentrancyGuard, Ownable {

    // Enum defining the possible states of a job
    enum JobStatus { OPEN, ASSIGNED, AWAITING_APPROVAL, COMPLETED, REFUNDED, DISPUTED }

    // Struct representing a job posting
    struct Job {
        uint256 id;             // Unique identifier for the job
        string title;           // Title of the job
        string descriptionCID;  // <<< CORRECTED: IPFS CID for the detailed description
        uint256 budget;         // Budget for the job in wei
        address payable employer; // Address of the employer who posted the job
        address payable freelancer; // Address of the freelancer assigned (initially address(0))
        JobStatus status;       // Current status of the job
        uint256 escrowTime;     // Timestamp when funds were escrowed
    }

    uint256 public jobCount; // Counter for total jobs posted, used for assigning IDs
    mapping(uint256 => Job) public jobs; // Mapping from job ID to Job struct
    mapping(uint256 => uint256) public escrowedFunds; // Mapping from job ID to escrowed amount

    uint256 public constant REFUND_DELAY = 7 days; // Delay before employer refund if OPEN

    // --- Events ---
    // <<< CORRECTED: Event includes descriptionCID >>>
    event JobPosted(uint256 indexed jobId, address indexed employer, string title, uint256 budget, string descriptionCID);
    event JobApplied(uint256 indexed jobId, address indexed freelancer);
    event PaymentEscrowed(uint256 indexed jobId, address indexed employer, uint256 amount);
    event PaymentReleased(uint256 indexed jobId, address indexed freelancer, uint256 amount);
    event EmployerRefunded(uint256 indexed jobId, address indexed employer, uint256 amount);
    event DisputeRaised(uint256 indexed jobId, address indexed initiator);
    event DisputeResolved(uint256 indexed jobId, address indexed admin, address indexed winner, uint256 amount);
    event WorkSubmitted(uint256 indexed jobId, address indexed freelancer);

    // --- Modifiers ---
    modifier onlyEmployer(uint256 _jobId) {
        require(msg.sender == jobs[_jobId].employer, "JobBoard: Caller is not the employer");
        _;
    }

    modifier jobExists(uint256 _jobId) {
        require(_jobId > 0 && _jobId <= jobCount, "JobBoard: Job does not exist");
        _;
    }

    constructor() Ownable(msg.sender) {}

    // --- Core Functions ---

    /**
     * @notice Allows anyone to post a new job.
     * @dev Creates a job entry storing description CID off-chain.
     * @param _title The title of the job.
     * @param _descriptionCID The IPFS CID for the job description. <<< CORRECTED PARAM NAME
     * @param _budget The payment amount for the job in wei.
     */
    function postJob(
        string memory _title,
        string memory _descriptionCID, // <<< CORRECTED PARAM NAME
        uint256 _budget
    ) public {
        require(bytes(_title).length > 0, "JobBoard: Title cannot be empty");
        // <<< ADDED: Check if description CID is provided >>>
        require(bytes(_descriptionCID).length > 0, "JobBoard: Description CID cannot be empty");
        require(_budget > 0, "JobBoard: Budget must be greater than 0");

        jobCount++;
        uint256 currentJobId = jobCount;

        // <<< CORRECTED: Typo 'jobS' fixed to 'jobs' >>>
        // <<< CORRECTED: Struct assignment matches struct field name and param name >>>
        jobs[currentJobId] = Job({
            id: currentJobId,
            title: _title,
            descriptionCID: _descriptionCID, // Use correct field and param name
            budget: _budget,
            employer: payable(msg.sender),
            freelancer: payable(address(0)), // No freelancer assigned initially
            status: JobStatus.OPEN,
            escrowTime: 0 // Escrow time is set when funds are actually escrowed
        });

        // <<< CORRECTED: Event emission includes descriptionCID >>>
        emit JobPosted(currentJobId, msg.sender, _title, _budget, _descriptionCID);
    }

    /**
     * @notice Allows the employer to escrow the payment for an open job.
     * @param _jobId The ID of the job to escrow funds for.
     */
    function escrowFunds(uint256 _jobId) public payable jobExists(_jobId) onlyEmployer(_jobId) {
        Job storage job = jobs[_jobId];

        require(job.status == JobStatus.OPEN, "JobBoard: Job must be open");
        require(msg.value == job.budget, "JobBoard: Sent ETH must match job budget");
        require(escrowedFunds[_jobId] == 0, "JobBoard: Funds already escrowed");

        escrowedFunds[_jobId] = msg.value;
        job.escrowTime = block.timestamp;

        emit PaymentEscrowed(_jobId, msg.sender, msg.value);
    }

    /**
     * @notice Allows anyone (except employer) to apply for (be assigned to) an open, escrowed job.
     * @param _jobId The ID of the job to apply for.
     */
    function applyForJob(uint256 _jobId) public jobExists(_jobId) {
        Job storage job = jobs[_jobId];

        require(job.status == JobStatus.OPEN, "JobBoard: Job is not open");
        require(job.freelancer == address(0), "JobBoard: Job already assigned");
        require(escrowedFunds[_jobId] == job.budget, "JobBoard: Funds not escrowed by employer");
        require(msg.sender != job.employer, "JobBoard: Employer cannot apply for their own job");

        job.freelancer = payable(msg.sender);
        job.status = JobStatus.ASSIGNED;

        emit JobApplied(_jobId, msg.sender);
    }

   /**
    * @notice Allows the assigned freelancer to mark the work as completed (ready for approval).
    * @param _jobId The ID of the job being marked as done.
    */
    function markWorkDone(uint256 _jobId) public jobExists(_jobId) nonReentrant {
        Job storage job = jobs[_jobId];

        require(msg.sender == job.freelancer, "JobBoard: Only assigned freelancer can mark work done");
        require(job.status == JobStatus.ASSIGNED, "JobBoard: Job must be ASSIGNED to mark as done");

        job.status = JobStatus.AWAITING_APPROVAL;

        emit WorkSubmitted(_jobId, msg.sender);
    }

    /**
     * @notice Allows the employer to release the escrowed payment to the freelancer after work submission.
     * @param _jobId The ID of the job for which to release payment.
     */
    function releasePayment(uint256 _jobId) public jobExists(_jobId) onlyEmployer(_jobId) nonReentrant {
        Job storage job = jobs[_jobId];
        uint256 amount = escrowedFunds[_jobId];

        require(job.status == JobStatus.AWAITING_APPROVAL, "JobBoard: Work must be submitted (AWAITING_APPROVAL)");
        require(job.freelancer != address(0), "JobBoard: No freelancer assigned");
        require(amount == job.budget && amount > 0, "JobBoard: Escrow amount mismatch or zero");

        job.status = JobStatus.COMPLETED;
        escrowedFunds[_jobId] = 0; // Clear escrow balance

        (bool success, ) = job.freelancer.call{value: amount}("");
        require(success, "JobBoard: Payment transfer failed");

        emit PaymentReleased(_jobId, job.freelancer, amount);
    }

    /**
     * @notice Allows the employer to reclaim escrowed funds if the job is still OPEN after REFUND_DELAY.
     * @param _jobId The ID of the job for which to request a refund.
     */
    function refundEmployer(uint256 _jobId) public jobExists(_jobId) onlyEmployer(_jobId) nonReentrant {
        Job storage job = jobs[_jobId];
        uint256 amount = escrowedFunds[_jobId];

        require(job.status == JobStatus.OPEN, "JobBoard: Job must be open (no freelancer assigned)");
        require(amount > 0, "JobBoard: No funds escrowed");
        require(job.escrowTime > 0, "JobBoard: Funds were never escrowed"); // Sanity check
        require(block.timestamp >= job.escrowTime + REFUND_DELAY, "JobBoard: Refund delay not passed");

        job.status = JobStatus.REFUNDED;
        escrowedFunds[_jobId] = 0; // Clear escrow balance

        (bool success, ) = job.employer.call{value: amount}("");
        require(success, "JobBoard: Refund transfer failed");

        emit EmployerRefunded(_jobId, job.employer, amount);
    }

    /**
     * @notice Allows employer or freelancer to raise a dispute on ASSIGNED or AWAITING_APPROVAL jobs.
     * @param _jobId The ID of the job to dispute.
     */
    function raiseDispute(uint256 _jobId) public jobExists(_jobId) {
        Job storage job = jobs[_jobId];

        require(msg.sender == job.employer || msg.sender == job.freelancer, "JobBoard: Only employer or freelancer can raise dispute");
        require(job.status == JobStatus.ASSIGNED || job.status == JobStatus.AWAITING_APPROVAL, "JobBoard: Job must be ASSIGNED or AWAITING_APPROVAL to dispute");

        job.status = JobStatus.DISPUTED;

        emit DisputeRaised(_jobId, msg.sender);
    }

    /**
     * @notice Allows the contract owner (admin) to resolve a dispute.
     * @param _jobId The ID of the disputed job.
     * @param _awardToFreelancer Boolean indicating if funds go to freelancer (true) or employer (false).
     */
    function resolveDispute(uint256 _jobId, bool _awardToFreelancer) public onlyOwner nonReentrant {
        require(_jobId > 0 && _jobId <= jobCount, "JobBoard: Job does not exist"); // Keep basic check
        Job storage job = jobs[_jobId];
        uint256 amount = escrowedFunds[_jobId];

        require(job.status == JobStatus.DISPUTED, "JobBoard: Job not in dispute");
        require(amount > 0, "JobBoard: No funds in escrow to resolve");

        escrowedFunds[_jobId] = 0; // Clear escrow

        address payable winner;
        bool success;

        if (_awardToFreelancer) {
            require(job.freelancer != address(0), "JobBoard: Cannot award to non-existent freelancer");
            winner = job.freelancer;
            job.status = JobStatus.COMPLETED; // Consider COMPLETED if freelancer wins dispute
            (success, ) = winner.call{value: amount}("");
            require(success, "JobBoard: Dispute payment to freelancer failed");
            emit PaymentReleased(_jobId, winner, amount);
        } else {
            winner = job.employer;
            job.status = JobStatus.REFUNDED; // Consider REFUNDED if employer wins dispute
            (success, ) = winner.call{value: amount}("");
            require(success, "JobBoard: Dispute refund to employer failed");
            emit EmployerRefunded(_jobId, winner, amount);
        }

        emit DisputeResolved(_jobId, msg.sender, winner, amount);
    }

    // --- View Functions ---

    function getJobCount() public view returns (uint256) {
        return jobCount;
    }

    // Returns the job struct, now containing descriptionCID instead of description
    function getJob(uint256 _jobId) public view jobExists(_jobId) returns (Job memory) {
        return jobs[_jobId];
    }

    function getEscrowAmount(uint256 _jobId) public view jobExists(_jobId) returns (uint256) {
        return escrowedFunds[_jobId];
    }
}

